import {
  TouchableWithoutFeedback,
  ImageBackground,
  View,
  Keyboard,
  Text,
} from "react-native";
import { globalStyles } from "../styles/styles";
import { Login } from "../TheLogin/Login";
import { WaveThingy } from "../TheLogin/WaveThingy";

export default function LoginRegister({ navigation }) {
  const image = { uri: "https://reactjs.org/logo-og.png" };
  return (
    <ImageBackground
      source={require("../../assets/fonts/bg4.jpeg")}
      style={{
        width: "100%",
        height: "100%",
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <TouchableWithoutFeedback
        onPress={() => {
          Keyboard.dismiss();
        }}
      >
        <View style={globalStyles.container}>
          <WaveThingy />
          <View
            style={{
              borderWidth: 1,
              borderColor: "white",
              borderStyle: "solid",
              borderRadius: 6,
              width:"25%",
              padding:"1%",
            //   position:"absolute",
              top:'7%',
              left:"70%",
              justifyContent:"flex-end",
              textAlign: "center",
            }}
          >
            <Text
              style={{
                fontSize: 19,
                paddingBottom: 7,
                marginRight: 6,
                fontWeight: "bold",
                color: "white",
              }}
            >
              S'inscrire
            </Text>
          </View>
          <Login onPress={() => navigation.navigate("Home")} />
        </View>
      </TouchableWithoutFeedback>
    </ImageBackground>
  );
}
